// Start only one producer
if (!globalThis.myProducer || !globalThis.myProducer.face.running) {
    console.log("Starting Producer P");
    const { Data } = ndn.packet;
    const { toUtf8 } = ndn.util;

    const endpoint = node.nfw.getEndpoint({ secure: false });
    globalThis.myProducer = endpoint.produce('/ndn/producer/test', async (interest) => {
        const data = new Data(interest.name, Data.FreshnessPeriod(5000));
        data.content = toUtf8("Hello NDN!");
        return data;
    });
}

