// Start only one producer
if (!window.myProducer || !window.myProducer.face.running) {
    console.log("Starting Producer P");
    const { Data } = ndn.packet;
    const { toUtf8 } = ndn.tlv;

    const endpoint = node.nfw.getEndpoint({ secure: false });
    window.myProducer = endpoint.produce('/ndn/producer/test', async (interest) => {
        const data = new Data(interest.name, Data.FreshnessPeriod(5000));
        data.content = toUtf8("Hello from NDN producer!");
        return data;
    });
}
